insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151137c078c0003','陈医生','心血管内科 副主任医师，从事临床教学、科研10余年，具有丰富的临床工作经验。<br />\r\n擅长心血管疾病的诊断与治疗，尤其是心血管内科危重症的抢救及心血管疾病的介入治疗。','心血管内科','心血管内科',NULL,NULL,'/upload/qc/tupian1_2015122214192981.jpg',1,NULL,'副主任医师',NULL,NULL,NULL,'各种奖励','同济医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151137f48830004','谭医生','心血管内科 副主任医师，中华医学会心血管分会会员。从事心内科临床工作二十余年。<br />\r\n曾在上海、广州、武汉等医院心内专科进修。撰写发表论文多篇。对心血管系统疾病的诊断、治疗具有丰富的经验。对冠心病的治疗具有独到的见解。','心血管内科','心血管系统疾病的诊断',NULL,NULL,'/upload/qc/tupian2_20151223101438792.png',2,NULL,'副主任医师',NULL,NULL,NULL,'中华医学会心血管分会会员','中华医学会');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511380f1130005','杨医生','主任医师，中国康复医学会儿童康复专业委员会常务委员，中国医师协会康复医师分会儿童康复专业委员会委员，从事儿科及儿童康复医疗工作，主要研治脑瘫及其他疾病引起的瘫痪、语言障碍、智力低下等。<br />\r\n曾有三项课题分别获市科技二、三等奖，10余篇论文在国家级及省级发表，撰写医学诊疗书三本。曾被评为市&ldquo;十佳医生&rdquo;、市&ldquo;职业道德爽十佳&rdquo;先进个人、市&ldquo;五一&rdquo;劳动奖章。<br />\r\n&nbsp;','儿科','儿科及儿童康复医疗',NULL,NULL,'/upload/qc/tupian3_20151223101526481.png',2,NULL,'主任医师',NULL,NULL,NULL,'三项课题分别获市科技二、三等奖。','中国康复医学会儿童康复专业委员会常务委员');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151138b423c0008','丁医生','主任医师，硕士生导师，市重点专业（小儿哮喘）学科带头人。从事小儿呼吸专业30余年，在小儿呼吸系统诊治，特别是呼吸系统疑难病诊治有丰富经验。<br />\r\n擅长治疗各种呼吸道感染（肺炎、反复呼吸道感染）、哮喘、婴幼儿喘息性疾病、慢性咳嗽等。','儿科','各种呼吸道感染',NULL,NULL,'/upload/qc/tupian4_20151223101621186.png',2,NULL,'主任医师',NULL,NULL,NULL,'第七届”中国医师奖“','济南儿童医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151138f3c940009','法医生','从事心外科近20年，完成各类心脏手术1000多例。手术方式和效果得到国内行业的赞许。<br />\r\n复杂先天性心脏病手术成功率近100％。<br />\r\n发表论文20余篇，专著6部，获得省科技进步二等奖2项，三等奖3项，曾被评为省青年科技专家、跨世纪学术技术带头人。','心外科','心外科',NULL,NULL,'/upload/qc/tupian5_2015122310180633.jpg',1,NULL,'主任医师',NULL,NULL,NULL,'省科技精兵二等奖2项，三等奖3想。','郑州大学第二附属医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511393db0c000a','李医生','主任医师教授，医学硕士，湖北省及武汉市普外学会、腹腔镜学会、胰腺病学会、抗癌专业副主任委员。<br />\r\n擅长肝癌、胰腺癌、肝硬化门静脉高压症的手术治疗。以及肝胆胰外科腹腔镜手术，包括腔镜下胆囊切除、脾期初、肝脏部分切除术，门脉高压断流术。<br />\r\n&nbsp;','肝胆胰外科','肝胆胰外科',NULL,NULL,'/upload/qc/tupian6_2015122310185658.png',1,NULL,'主任医师',NULL,NULL,NULL,'各种奖励','同济医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d3015114011d97000c','赵医生','中华医学工程学分会干细胞工程专业委员会委员，中国医师学会细胞科技应用专业委员会专家委员，中华糖尿病学会血管病和足病学组成员、省糖尿病学会副主委、内分泌学会常委、血液学会常委。<br />\r\n擅长糖尿病及并发症早期干预、干细胞技术治疗糖尿病及慢性并发症、甲状腺、肾上腺、垂体等内分泌代谢疾病诊治。','内分泌科','贫血、出血性疾病、白血病、血液肿瘤、糖尿病、甲亢等内分泌代谢疾病的诊治',NULL,NULL,'/upload/qc/tupian7_20151223101934788.png',2,NULL,'主任医师',NULL,NULL,NULL,'获武汉市科技进步一等奖、三等奖，湖北省科技进步二等奖，湖北省卫生科技进步三等奖，在中华系列、核心、统计源等期刊发表专业论文40余篇。','中华血液学会武汉分会常委');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511405e8d1000d','宋医生','主任医师、医学博士，中华妇产科学会湖北省妇产科分会常务委员。湖北省医师协会妇产科分会委员，武汉市医疗事故鉴定委员会专家。<br />\r\n从事妇产科30余年，擅长妇科肿瘤及不孕症的规范化治疗、女性更年期技术替代治疗及女性生殖道感染性疾病的诊断和处理，熟练高位妊娠，围产医学，遗传、有声咨询和妊娠合并症的诊断处理。','妇产科','擅长妇科肿瘤及不孕症的规范化治疗',NULL,NULL,'/upload/qc/tupian8_20151223102015616.png',2,NULL,'主任医师',NULL,NULL,NULL,'曾获”全国十大妇幼天使“、”十大妇产医生“','武汉市妇女儿童医疗保健中心');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511444d58c000f','岳医生','1985年毕业于苏州大学医学院，从事儿科临床工作二十余年。<br />\r\n曾在北京儿童医院儿内科进修一年，主攻小儿肾脏、泌尿系统疾病，擅长小儿呼吸系统疾病、小儿消化系统疾病、肾脏病、急危重病的救治。','儿科','擅长小儿呼吸系统疾病',NULL,NULL,'/upload/qc/tupian9_20151223102049360.png',1,NULL,'主任医师',NULL,NULL,NULL,'各种奖励','南方医科大学第三附属医院消化内科');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511447f6470010','陈医生','主任医师、青岛第八人民医院泌尿科主任，对本专业疑难杂症及危急重病人的诊治处理具有较高的水平，并积累了丰富的经验。<br />\r\n尤其擅长泌尿外科微创技术，全面开展了腹腔镜下肾上腺肿瘤、肾盂成型、膀胱全切等；开展了女性压力性尿失禁吊带治疗；小儿尿道下裂一期成型手术等小儿泌尿外科的常规手术。','泌尿科','擅长泌尿外科微创技术',NULL,NULL,'/upload/qc/tupian10_20151223102117536.png',1,NULL,'主任医师',NULL,NULL,NULL,'各种奖励','青岛第八人民医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151182558e90013','何医生','武汉民馨医院首席特聘专家。曾任湖北省肿瘤医院乳腺科主任，从事乳腺疾病的诊断、手术40多年，积累了丰富的临床经验。<br />\r\n总结了一整套乳腺常见病、多发病及乳腺癌的治疗方法。在武汉三镇乳腺病患者心目中有极高的声誉，俗称&ldquo;何神手&rdquo;。','乳腺科','乳腺常见病、多发病及乳腺癌的治疗',NULL,NULL,'/upload/qc/13418103311589_20151223102404393.jpg',1,NULL,'主任医生',NULL,NULL,NULL,'各种奖励','武汉民馨医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511828fb080014','樊医生','主任医师 教授 湖北省普通外科学会常委；湖北省胰腺病学会副主任委员；省医院协会常务理事、湖北省医师协会理事；毕业于同济医科大学，享受国务院津贴，国家专业技术二级专家，从事外科临床工作四十余年，对肝胆胰、胃肠道及乳腺、甲状腺等疾病有深入的研究，曾多次获湖北省卫生厅科研进步奖。','外科','肝胆胰、胃肠道及乳腺、甲状腺等疾病的诊疗',NULL,NULL,'/upload/qc/W020150707254642533431_20151223102854884.jpg',1,NULL,'主任医师 教授',NULL,NULL,NULL,'多次获湖北省卫生厅科研进步奖','同济医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151182cc54b0015','杨医生','从事妇产科临床工作三十余年，长期担任三甲医院妇产科主任，曾多次在国内著名学府学习进修，博采众长，对妇产科各种常见、多发病症的诊疗，均有很高造诣，尤其擅长各种妇产科危重症的诊治。<br />\r\n&nbsp;','妇产科','各种妇产科危重症的诊治',NULL,NULL,'/upload/qc/u=4140400916,424271249&fm=15&gp=0_20151223103054795.jpg',2,NULL,'妇产科主任',NULL,NULL,NULL,'各种奖励','同济医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151184f3ca00017','邓医生','毕业于国内著名学府，从事临床、教学工作四十余年，长期担任三甲医院大内科主任、教授，对内科常见、多发病及各种疑难杂症均有很高的造诣，尤其擅长心脑血管疾病的诊疗。湖北省医学会内科学会常委，湖北省医学会心血管学会常委，湖北省医学会医疗事故鉴定专家。','内科','心脑血管疾病的诊疗',NULL,NULL,'/upload/qc/1307021012076483380_20151223103343873.jpg',1,NULL,'主任医师',NULL,NULL,NULL,'三次荣立集体三等功，1992年荣立个人三等功次。被授予公司标兵和优秀共产党员荣誉称号。','武钢职工总医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151185207230018','彭医生','湖北省中山医院肿瘤科副主任，肿瘤热疗中心副主任，主任医师，从事肿瘤内科临床专业工作30余年。擅长各种肿瘤的诊断，新辅助化疗及术后辅助化疗，姑息性热化疗，恶性胸腹水的腔内热化疗，晚期恶性肿瘤的止痛及营养支持治疗等。','肿瘤科','恶性胸腹水的腔内热化疗，晚期恶性肿瘤的止痛及营养支持治疗',NULL,NULL,'/upload/qc/96371313451177954_20151223103445606.jpg',2,NULL,'主任医师',NULL,NULL,NULL,'各种奖励','湖北省中山医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511854e10b0019','马医生','全国著名肾病专家，主任医师、教授、硕士生导师，从事肾脏病临床教学工作近40年，在国内治疗各种肾病，享有极高盛誉。曾为湖北省中西医结合肾病学会副主任委员，湖北省神病学会常务委员，湖北省透析协会副主任委员。','肾病科','中西医结合治疗慢性肾炎、糖尿病肾病、狼疮性肾炎、尿路感染和慢性肾衰竭',NULL,NULL,'/upload/qc/W020140115580122193451_20151223103604500.jpg',2,NULL,'主任医师',NULL,NULL,NULL,'各种奖励','湖北省中山医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d301511861386b001a','刘医生','从事内科工作近30年，积累了丰富的临床经验，曾在专业刊物上发表论文数10篇，收到业内人士的好评。对内科的常见病、多发病的诊治有较深的造诣，特别对久治不愈的咳嗽、急慢性胃肠炎、胃溃疡、急性泌尿系感染、糖尿病、高血压、高血压性心脏病、头昏、头痛、眩晕症等有独特的治疗方法。其专业的技术水平、高尚的行医情操，赢得了广大患者的信任和青睐。<br />\r\n&nbsp;','内科','糖尿病、高血压、高血压性心脏病',NULL,NULL,'/upload/qc/89811371544147_2015122310371855.jpg',2,NULL,'内科副主任',NULL,NULL,NULL,'各种奖励','同济医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151187a61b5001c','孙医生','<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:10.5pt\">现任湖北</span><a href=\"http://www.zhzyw.org/\"><span style=\"color:rgb(153, 0, 0); font-family:宋体; font-size:10.5pt\">中医</span></a><span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:10.5pt\">学院副院长、教授。博士生导师。</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:10.5pt\">1958年被送入湖北中医学院，读书期间使他开阔了眼界，精心致意的博览古典医籍，铸就了厚实的中医学术功底。</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:10.5pt\">20多年来，一直勤勤恳恳，踏踏实实地从事中医学的教学、科研、临床以及行政管理工作，他承担了不少中药的科研任务，他在</span><a href=\"http://www.zhzyw.org/zyts/zyzj/\"><span style=\"color:rgb(153, 0, 0); font-family:宋体; font-size:10.5pt\">针灸</span></a><span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:10.5pt\">学术上有较高造诣，在中国针灸界有一定知名度。他在学术上潜心研习针灸，他继承导师亲授并博取历代诸家之长，并综合自己的研究心得和临床经验，对消化系统疾病的针灸治疗有所擅长，尤其用子午流珠纳甲法治疗胃及十二之长溃疡疗效显著。</span></p>\r\n','消化内科','对消化系统疾病的针灸治疗有所擅长',NULL,NULL,'/upload/qc/fd797ad21229279c9cf73dcb66f9b133_20151223104421958.jpg',1,NULL,'教授、博士生导师',NULL,NULL,NULL,'各种奖励','湖北省中医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f511357d30151188b713d001d','朱医生','<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">曾任全国中医骨伤科学会副秘书长，</span><a href=\"http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=7851775\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">中央保健委员会</span></a><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">会诊专家，全国中西医结合骨伤科学会副理事长，</span><a href=\"http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=245991\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">中国中医科学院</span></a><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">骨伤科研究所所长。</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">精通中医学理论，长期从事中医、中西医结合骨伤科研究，临床及教学工作。师从中医名家，擅长治疗颈椎病、腰椎间盘突出等各种脊柱操作性疾病，</span><a href=\"http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=300423\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">跌打损伤</span></a><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">、骨折脱位、骨感染、骨髓炎、关节炎、肩周炎、类风湿性关节、</span><a href=\"http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=4184376\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">强直性脊柱炎</span></a><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">，对</span><a href=\"http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=122096\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">软组织损伤</span></a><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:12pt\">、颈、胸、腰椎等脊椎疾患更有深入的临床经验与独特疗效。</span></p>\r\n','骨科','治疗颈椎病、腰椎间盘突出等脊柱操作性疾病',NULL,NULL,'/upload/qc/0824ab18972bd407cde50e377a899e510eb309de_20151223104915420.jpg',1,NULL,'博士生导师',NULL,NULL,NULL,'各种奖励','湖北省中医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f516166d80151c782f3f6001e','于医生','<span style=\"background-color:rgb(247, 247, 247); color:rgb(62, 62, 62); font-family:microsoft yahei,arial,simsun,sans-serif; font-size:12px\">于信玲，女，副主作医师，擅长以内儿科为主的全科医疗，对脑血管疾病、糖尿病、风湿性疾病、精神心理疾病等的诊断治疗护理康复及急诊急救工作有自己的独到之处。</span>','中医科','失眠、痹证、淋证、头痛、中风、心悸、眩晕、消渴、郁证、痰饮等中医内科疾病。',NULL,NULL,'/upload/qc/o3846cxi2h3kf467_20151223105608376.jpg',2,NULL,'副主任医师',NULL,NULL,NULL,'各种奖励','长春中医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51c855fe0151cce453a10017','于医生','从事妇产科临床工作三十余年，泰州市医学会医疗损害鉴定专家组成员，中华医学会会员，省中西医结合学会会员，微创妇科手术上万例无事故专家；多次参加省内外妇科学术交流和培训，曾在复旦大学附属妇产科医院及上海杨浦中心医院妇科腔镜中心进修，参与编写《实用女性保健》，曾荣获泰州市自然科学奖和科技进步奖。','妇产科','对妇科领域的常见病、多发病及疑难杂症有较丰富的临床诊断和治疗经验，擅长妇科恶性肿瘤的诊治及手术。',NULL,NULL,'/upload/qc/201511121356224393_20151223113134222.gif',2,NULL,'妇产科主任 副主任医师 副教授',NULL,NULL,NULL,'各种奖励','泰州市妇幼保健医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51c855fe0151cce9eef00019','姜医生','毕业于南京医科大学，从事妇产科临床工作近三十年，多次参加省内外妇科学术交流和培训，曾在省肿瘤医院进修。在国家级医学杂志发表《腹腔镜辅助下阴式全子宫切除术的实用价值》等学术论文数篇。','妇产科','对妇科领域的常见病、多发病及疑难杂症有较丰富的临床诊断和治疗经验，擅长妇科恶性肿瘤的诊治及手术。',NULL,NULL,'/upload/qc/201511121356017989_20151223113741654.gif',2,NULL,'妇科主任 副主任医师 副教授',NULL,NULL,NULL,'各种奖励','泰州市妇女儿童医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151eb482e070002','朱医生','<span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:14px\">毕业于皖南医学院，至今已有三十余年外科、血管瘤诊疗经验，曾两次在三甲级医院进修，担任科主任职务，有丰富的临床血管瘤诊断及治疗经验。从医以来诊治患者无数，以其精湛的医术，在患者中深受好评。</span>','心脑血管','，尤其擅长对鲜红斑痣、草莓状血管瘤、混合型血管瘤进行有效治疗，对每一位患者都能给予最适合的治疗方案。',NULL,NULL,'/upload/qc/1_20151229090913979.png',1,NULL,'副主任医师',NULL,NULL,NULL,'在三十余年的诊疗生涯中，曾在国家级、省级医疗学术杂志中发表论文十余篇，取得学术成果无数。','上海曹安医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151eb4e77890004','姜医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:14px\">毕业于温州医学院临床医学系，从事妇产科，计划生育，妇幼保健工作三十余年，曾担任县计划生育技术指导站站长，妇幼保健院院长、书记等职，期间在中国人口计划生育杂志，省级医学刊物发表文章8篇。并多次赴省、市级医院深造。具有丰富的临床工作经验，擅长妇科常见病多发病及疑难杂症的诊治，熟练掌握子宫、卵巢、附件的恶、良性肿瘤切除术，显微镜下输卵管吻合术、宫颈手术、阴道前后壁修补术、人工阴道形成术，妇科整形术等。对月经失调、闭经，子宫内膜炎异位症，卵巢早衰、卵巢综合症等内分泌疾病的诊断治疗具有独特之道。</span>','妇产科','妇科常见病、多发病的处理',NULL,NULL,'/upload/qc/14-150414160I3R6_20151229091603750.jpg',2,NULL,'妇幼保健院院长',NULL,NULL,NULL,'各种奖励','绍兴城东医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151eb520b200006','杨医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:14px\">1983年毕业于石河子大学医学院，大学本科。从事内科专业工作近30年，先后在上海第二军医大学附属长海医院和浙江大学附属邵逸夫医院消化内科进修，工作表现出色。擅长诊治食道、胃、肠及肝、胆、胰腺等脏器的病症，对内科疑难、复杂疾病有较丰富的诊治经验，能熟练运用电子胃镜、肠镜和十二指肠进行检查和治疗，能开展肝癌、肝硬化、胰腺肿瘤等疾病的穿刺活检及PEI等难度较高的治疗操作。</span>','内科','擅长诊治食道、胃、肠及肝、胆、胰腺等脏器的病症，对内科疑难、复杂疾病有较丰富的诊治经验',NULL,NULL,'/upload/qc/16-141124143105Z0_20151229092001200.jpg',1,NULL,'主任医师',NULL,NULL,NULL,'曾在各级医学杂志上发表论文30余篇，发表医学科普文章370余篇','绍兴市人民医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151eb544b310008','尤医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:14px\">现为中华医学会胸心血管外科浙江省分会会员，曾被绍兴市医学会聘为医疗事故技术鉴定、医疗损害鉴定、非法行医鉴定医师库医师。在本地区胸外科领域勇于开拓，积极进取开展多项有意义的实践与研究，对胸外科恶性肿瘤有较高的诊治技术。主刀完成胸部各类重大手术2000余例。率先在绍兴开展高位食管癌经颈、胸、腹三切口食管癌根治性切除术，慢性脓胸胸膜剥脱术。&nbsp;</span>','心胸外科','擅长胸部外科各类良、恶性疾病及复杂胸部创伤的诊疗。对肺癌、食管癌、贲门癌、胸腺癌、纵膈、膈肌、胸壁、胸膜及胸部疑难杂症等疾病的诊断与治疗具有丰富的临床经验和熟练规范的手术操作技能',NULL,NULL,'/upload/qc/16-141124143042162_20151229092228638.jpg',1,NULL,'副主任医师',NULL,NULL,NULL,'在省级以上杂志发表论文10余篇','绍兴城东医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151eb5a8f0f000a','余医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:14px\">省消化内镜中心委员、绍兴市消化学会副主任、绍兴市医疗鉴定医师库成员。从事内科临床33年，多年主持消化科科研、教学、医疗等工作，先后在全国、省级杂志上发表论文20多篇，定期为基层协作医院进行技术业务指导，在绍兴市有较高知名度。</span>','消化内科','擅长消化科疑难杂症及内镜下的诊断及治疗。',NULL,NULL,'/upload/qc/16-141124142I4a8_20151229092919229.jpg',1,NULL,'主任医师',NULL,NULL,NULL,'先后在全国、省级杂志上发表论文20多篇','浙江大学医学院附属第二医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f091cc31000e','叶医生','<span style=\"color:rgb(34, 34, 34); font-family:arial,verdana,sans-serif; font-size:16px\">毕业于浙江中医学院。从事本专业五十余年。善用多种手法对颈椎病、腰椎盘突出症、关节炎、肩周炎、风湿关节炎、类风湿关节炎、牙痛、三叉神经痛、坐骨神经痛、偏头痛、中风后遗症、面神经瘫痪等疾病的治疗。同时对治疗各种胃痛、肾病、肝病、哮喘、咳嗽有独到之处。</span>','中医科','善用多种手法对颈椎病、腰椎盘突出症、关节炎、肩周炎、风湿关节炎、类风湿关节炎、牙痛、三叉神经痛、坐骨神经痛、偏头痛、中风后遗症、面神经瘫痪等疾病的治疗',NULL,NULL,'/upload/qc/15-150506110002E6_20151230094745294.jpg',1,NULL,'副主任医师',NULL,NULL,NULL,'各种奖励','浙江省人民医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f093d9fd0010','黄医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:16px\">毕业于原浙江医科大学，1988进修于浙医二院神经外科，现任浙江省神经外科学会会员，省抗癌协会会员，绍兴市神经外科学会副主委，市医疗事故技术鉴定及病残儿童鉴定医师库成员，绍兴市药品（医疗器械）不良反应检测中心医师咨询委员会成员。在全国及省级杂志发表论文十余篇，其中《脑动静脉畸形出血血肿清除+病灶清除》获绍兴市科技优秀论文三等奖，84-85年度获全市卫生系统社会主义劳动竞赛《百例手术无感染》优胜个人称号。合作研究《一氧化氮、内皮素在脑出血中的动态研究》课题获绍兴市科技成果鉴定国内先进及县科技进步二等奖。</span>','神经外科','擅长治疗各种颅脑损伤、脑肿瘤、脑血管疾病及功能性神经外科疾病。',NULL,NULL,'/upload/qc/15-150HG503402O_20151230094955617.JPG',1,NULL,'主任医师',NULL,NULL,NULL,'在全国及省级杂志发表论文十余篇，其中《脑动静脉畸形出血血肿清除+病灶清除》获绍兴市科技优秀论文三等奖，84-85年度获全市卫生系统社会主义劳动竞赛《百例手术无感染》优胜个人称号','浙医二院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f09589d50012','王医生','<span style=\"color:rgb(0, 0, 0); font-family:simsun; font-size:18px\">在绍兴市人民医院普外科、肿瘤外科、肛肠外科工作近三十年，有丰富的临床工作经验。擅长重度痔疮及复杂性肛瘘的微创手术，以及内痔注射、套扎无痛治疗。对肛旁脓肿一次性切开根治手术治愈率高。对肛裂及肛门下坠不适及急慢性结肠炎、直肠炎用中西医结合治疗有丰富的临床经验。</span>','肛肠科','治疗内外痔、肛裂、肛瘘、急慢性结（直）肠炎和直肠大肠癌肿等常见肛肠疾病有独到之处',NULL,NULL,'/upload/qc/15-15121409161I06_20151230095148353.jpg',2,NULL,'主治医师',NULL,NULL,NULL,'各种奖励','绍兴市人民医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f096e6040014','周医生','<span style=\"color:rgb(34, 34, 34); font-family:arial,verdana,sans-serif; font-size:14px\">在市级医院从事内科临床工作40余年。在内科的常见病上有丰富的临床经验和独特的研究。针对老年人多发的心血管疾病等诊治有较深的造诣。擅长对内科的常见病以及急、危、重患者的抢救和疑难病症的诊断和治疗。</span>','内科','擅长对内科的常见病以及急、危、重患者的抢救和疑难病症的诊断和治疗。',NULL,NULL,'/upload/qc/15-15042110135N20_20151230095318969.png',2,NULL,'副主任医师',NULL,NULL,NULL,'各种奖励','温州医学院附属第一医院 ');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f0992e420016','胡医生','<span style=\"color:rgb(34, 34, 34); font-family:arial,verdana,sans-serif; font-size:14px\">浙江省中西医结合学会消化专业委员。从事中西医结合内科临床工作40余年，曾任绍兴市中医院内科主任、急诊科主任、防保科主任、门诊部主任等职。擅长消化道出血、胃溃疡、急慢性胃炎、慢性肠炎、胆囊炎、胆结石、消化不良等病的诊治及内窥镜检查。</span>','消化内科','擅长消化道出血、胃溃疡、急慢性胃炎、慢性肠炎、胆囊炎、胆结石、消化不良等病的诊治及内窥镜检查。',NULL,NULL,'/upload/qc/15-150421100021151_20151230095548570.png',1,NULL,'副主任医师',NULL,NULL,NULL,'各种奖励','台州医院');
insert  into `t_doctor`(`ID`,`NAME`,`DETAIL`,`DEPART`,`DOMAIN`,`MOBILE`,`TEL`,`HEAD`,`GENDER`,`AGE`,`LEVEL`,`BIRTHDAY`,`ONLINE`,`TYPE`,`REWARD`,`HOSPITAL`) values ('8a22dd0f51e8d54f0151f09bda2b0018','常医生','<span style=\"color:rgb(0, 0, 0); font-family:宋体; font-size:16px\">安徽医科大学临床医学系本科毕业，任安徽省亳州市蒙城一院外科主任20余年。从事普外科工作30余年，临床经验丰富，能处理普外科大中小疾病，主刀普外科大中小手术。对普外科、胸外科、泌尿外科、妇产科、肛肠外科、麻醉领域的诊疗有丰富的经验，尤其熟练主刀腹腔镜胆囊切除术。</span>','外科','对普外科、胸外科、泌尿外科、妇产科、肛肠外科、麻醉领域的诊疗有丰富的经验，尤其熟练主刀腹腔镜胆囊切除术。',NULL,NULL,'/upload/qc/15-15111111145QH_2015123009584485.jpg',1,NULL,'副主任医师',NULL,NULL,NULL,'善于引进、吸收和开展新技术新项目，参与主持多项课题，其中五项获阜阳地区科学技术进步奖','宁波市第一医院');
